import React,{useState} from 'react';
import { FaMoon,FaSun} from 'react-icons/fa';



function Header() {
   const [darkMode, setDarkMode] =useState(false);

   const themeSwitcher=()=>{
      let headerContent = document.querySelector('.header');
      let appBody = document.querySelector('.app_body');


      headerContent.classList.toggle('header_darkMode');

      setDarkMode(!darkMode)
   }


   return (
     <div className="header">
       <div className="header_container">
          <h1>Where in the world?</h1>
           <div className="switch_mode">
              <button onClick={themeSwitcher}>{darkMode ? <FaMoon/> : <FaSun/>}</button>

           <h3>{ darkMode ? 'DarkMode' :'LightMode' }</h3>
          </div>
         </div>
      </div>
   )
}

export default Header
