import React from 'react';
import { FaMoon,FaSun } from 'react-icons/fa';


function Header() {

   const sunSwitch=()=>{
      let moonIcon = document.querySelector('.moon');
      let themeText = document.querySelector('.content');
      let sunIcon = document.querySelector('.sun')


      sunIcon.addEventListener('click',()=>{
         moonIcon.style.display="block"
         sunIcon.style.display="none";
         document.body.classList.add('theme_default_bg'); 

         if(sunIcon.style.display==='block'){
            themeText.textContent="LightMode"

         }else if(moonIcon.style.display==='block'){
             themeText.textContent="DarkMode"
         }

      })


   };




    return (
       <section className="header_banner">
        <div className="header">
             <div className="header_title">
                <h3>Where in the world?</h3>

             </div>

             <div className="theme"> 
                <FaMoon className="moon"/>
                <FaSun  onClick={sunSwitch}className="sun"/>
                <h3 className="content">Light Mode</h3>
             </div>

        </div>

   </section>

    )
}

export default Header
