import React,{useState} from 'react';
import Country from './Components/Country';
import Header from './Components/Header';
import {Routes,Route} from 'react-router-dom'; 
import './Styles/styles.css';
import GroupOfCountries from './Components/GroupOfCountries';


function App() {

  const [darkMode,setDarkMode]=useState(false);

  const themeSwitcher=()=>{
      setDarkMode((prevState)=>!prevState)
  }

  return (
    <div className={`app ${darkMode ? 'darkMode' : ''}`}>
       <Header  onClick={themeSwitcher} darkMode={darkMode}/>
        <div className={`app_body ${darkMode ? 'darkMode' : ''}`}> 
          <Routes>
            <Route exact path="/" element={<GroupOfCountries darkMode={darkMode} />}/>
            <Route exact path="/:country" element={<Country darkMode={darkMode}/>}/>
          </Routes>
        </div>
   </div>
  )
}

export default App
