import React,{useState} from 'react';
import { FaMoon,FaSun} from 'react-icons/fa';
import InputFilter from './Input_filter';



function Header() {
   const [switchMode, setSwitchMode] =useState(false);

   const themeSwitcher=()=>{
      let headerContent = document.querySelector('.header');
      let appBody = document.querySelector('.app');
      let searchInputs = document.querySelector('.search_input');
      let dropDown = document.querySelector('.select_region');
      // let details_Container = document.querySelector('.countries');

      // let backBtn = document.querySelector('.back');
      // let border_details = document.querySelector('.border_country')


      headerContent.classList.toggle('header_darkMode');
      appBody.classList.toggle('appBody_darkMode');
      searchInputs.classList.toggle('search_input_darkMode');
      dropDown.classList.toggle('select_input_darkMode');
      // details_Container.classList.toggle('container_darkMode');

      // backBtn.classList.toggle('back_btn')
      // border_details.classList.toggle('border_country_darkMode');

      setSwitchMode(!switchMode)
   }


   return (
    <>
     <div className="header">
       <div className="header_container">
          <h1>Where in the world?</h1>
           <div className="switch_mode">
              <button onClick={themeSwitcher}>{switchMode ? <FaMoon className="moon"/> : <FaSun className="sun"/>}</button>

           <h3>{ switchMode ? 'DarkMode' : 'LightMode' }</h3>
          </div>
         </div>


      </div>

      </>
   )
}

export default Header
