import React,{useState, useRef} from 'react'
import Header from '../VerifiedUser/Header'
import { SearchInputContainer,SearchInput, SearchIcon,CloseIcon, SearchBarContainer, RenderData, RenderDataContent} from '../../Styles/Search.styled'
import { CloseOutlined, SearchOutlined } from '@ant-design/icons'
import axios from 'axios';

import {useNavigate} from 'react-router-dom'


// const base_url='https://api.hunter.io/v2/domain-search?domain=${}&api_key=f0968a7349aa4d21c1ae365c803781eba104dc38'


//657b622701ddfb316ee2d50026c5ddfa3643110d

function Domain() {
  const [information,setInformation]= useState({})
  const [filteredData,setFilteredData]=useState([])
  const [ima, setIma]=useState('')
  const [all , setAll] = useState([]);

 let load=filteredData
 let first_name=[]
 let display=[]





  const handleDetails=(code)=>{
    navigate(`/${code}`)
  }

  const clearContent=()=>{
    setFilteredData([])  
  }

  const searchDomain=useRef()

  const navigate=useNavigate()

  const searchData=()=>{
      const searchValues=searchDomain.current.value;
    if(searchValues.trim()){
      const getData=async()=>{
        const {data} = await axios.get(`https://api.hunter.io/v2/domain-search?domain=${searchValues}&api_key=657b622701ddfb316ee2d50026c5ddfa3643110d`)
       const image = searchValues.trim();
         setIma(`https://logo.clearbit.com/${image}`)


        setInformation(data)
        setFilteredData(data)
      }
      try {
        getData()

      } catch (error) {
        console.log(error.message)
      }

    }

   }


   const allEmails = load
   let mail=allEmails?.data?.emails;

   let everyone=mail?.filter(mail=>{
     if(mail.value.match([all])){
      display.push(mail.value)
     }
     if(mail.first_name){
      first_name.push(mail.first_name)
      console.log(first_name)
     }

   })

   const first = first_name?.map(first=><p>{first}</p>)
   const oneList=display?.map(one=><p>{one}</p>)
  return (
    <div>

      <div className='width_header'>
        <Header/>
      </div>

      <SearchBarContainer height={3.8} width={34}>
        <SearchInputContainer>
         <SearchIcon >
              <SearchOutlined/>
             </SearchIcon>

          <SearchInput placeholder='Search for domain names' ref={searchDomain} onChange={searchData} />

           <CloseIcon >
                <CloseOutlined onClick={clearContent}/>
                 </CloseIcon> 

         </SearchInputContainer>

        </SearchBarContainer>


   {filteredData.length !== 0 &&(
 <RenderData >

{ima?<img src={ima} alt="" className='search_img' />:''}
  <div className='base_content'>
   <p><span>Domain Name:{" "}</span>{filteredData.data?.domain}</p>
    <p className='organa'><span>Organization: </span>{filteredData.data?.organization}</p>   
    <p className='state'><span>State: </span>{filteredData.data?.state}</p> 
    </div>
   </RenderData>
 )}


    <div>

      <ul>
        <li>{first} </li>
        <p>{oneList}</p>
      </ul>
    </div>

    </div>
  )
}

export default Domain
