import React,{useState,useEffect,useRef} from 'react';
import {FaSearch} from 'react-icons/fa'
import axios from 'axios'


function GroupOfCountries({darkMode}) {
 const [countries,setCountry]=useState([]);
 const countryInputRef = useRef();
 const regionRef = useRef();


   useEffect(()=>{
      axios.get("https://restcountries.com/v2/all")
      .then(res=>{
         console.log(res)
         setCountry(res.data)
      })
      .catch(error=>console.log(error));

   },[])

   const searchCountries=()=>{
     const searchValue = countryInputRef.current.value;

     if(searchValue.trim()){
       const fetchSearch = async ()=>{
         const response = await fetch(`https://restcountries.com/v2/name/${searchValue}`);
         const data = await response.json();

         setCountry(data)
       }

       try{
         fetchSearch()
       }catch(error){
         console.log(error)
       }

   }else{
    axios.get("https://restcountries.com/v2/all")
    .then(res=>{
       console.log(res)
       setCountry(res.data)
    })
    .catch(error=>console.log(error));
   }

  }


  const selectRegion=()=>{
    const selectValue = regionRef.current.value;

    if(selectValue.trim()){
      const fetchSelect = async()=>{
        const response = await fetch(`https://restcountries.com/v2/region/${selectValue}`)
        const data = await response.json();

        if(selectValue === 'Filter by Region'){
             try{
              axios.get("https://restcountries.com/v2/all")
              .then(res=>{
                 console.log(res)
                 setCountry(res.data)
              })
              .catch(error=>console.log(error));
             }catch(error){
               console.log(error)
             }
             return;
        }

        setCountry(data);
      };

      try{
        fetchSelect();
      }catch(error){
        console.log(error)
      }
    }
  }


    return (
       <>
       <div className="input_base">

           <div className="inputs">
       <div className={`search_input ${darkMode ? 'darkMode' : ''}`}>
         <FaSearch className="search_icon"/>
         <input type="text" placeholder="Search for a country..." ref={countryInputRef} onChange={searchCountries}/>
       </div>
       <div className={`select_region ${darkMode ? 'darkMode':''}`}>
         <select className={`${darkMode ?'darkMode' : ''}`} ref={regionRef} onChange={selectRegion}>
           <option value="">All</option>
           <option value="">Africa</option>
           <option value="">America</option>
           <option value="">Asia</option>
           <option value="">Europe</option>
           <option value="">Oceania</option>
         </select>
       </div>
    </div>
        </div>
        <div className="countries">
           {countries.map((country)=>
               (
                <div className="whole_thing" >
                 <div className="flag_container">
                 <img src={country.flag} alt=""/>
               </div>

              <div className="details">
              <h3 className="name">{country.name}</h3>
              <p>Population:{""} <span className="values">{country.population}</span></p>
             <p>Regions:{""} <span className="values">{country.region}</span></p>
             <p>Capital:{""} <span className="values">{country.capital}</span></p>

            </div>
           </div> 


               )

           )}

    </div>

    </>
    )
}

export default GroupOfCountries
